import React, { useEffect, useState } from 'react';
import axios from 'axios';

import Budget from '../components/Budget';
import Method from '../components/Method';

export default () =>  {

    //RECORDS
    const [records, setRecords] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('https://finanzas.visssible.com/backend/apartados-consultar.php');
                setRecords(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []); // The empty dependency array ensures that this effect runs once on mount





    //Real amount

    let realAmount = 0;
    if (reorderedMethods.length >= 5) {
        realAmount = (
                        parseFloat(reorderedMethods[0].saldo) + (
                            parseFloat(reorderedMethods[1].saldo) + 
                            parseFloat(reorderedMethods[2].saldo) + 
                            parseFloat(reorderedMethods[3].saldo) + 
                            parseFloat(reorderedMethods[4].saldo)
                        )
                    ).toFixed(2);
    }

    //BUDGET COUNT

    const [sumOfAmounts, setSumOfAmounts] = useState(0);

    // Use async/await to wait for the data to be fetched before processing
    useEffect(() => {
        const calculateSumOfAmounts = async () => {
            let sum = 0;
            for (const record of records) {
                if (record.budget === 1) {
                    sum += parseFloat(record.amount);
                }
            }
            setSumOfAmounts(sum);
        };

        calculateSumOfAmounts();
    }, [records]); // This effect runs whenever the records state changes


    return (
        <div id="principal" className='pantalla completa' style={{paddingBottom: '66px'}}>

            <ul id='methods'>

                {reorderedMethods.length > 0 && (
                    <li key="0" className="Account">
                        <i className="fas fa-money-check-alt"></i>
                        <div className="name">{reorderedMethods[0].nombre}</div>
                        <div className="amount">{reorderedMethods[0].saldo}</div>
                        <div class="realAmount">$({realAmount})</div>
                    </li>
                )}

                <li id="balance">
                    <div class="founds"><i class="fas fa-wallet"></i> <span class="Label">Founds:</span> <span class="amount">$0</span></div>
                    <div class="budgets"><i class="fas fa-chart-pie"></i> <span class="Label">Budgets:</span> <span class="amount">${sumOfAmounts}</span> </div>
                    <div class="balance"><i class="fas fa-balance-scale"></i> <span class="Label">Balance:</span> <span class="amount">$0</span></div>
                </li>

                {reorderedMethods.slice(1).map((method, index) => (
                    <Method 
                        key={index} 
                        name={method.nombre} 
                        amount={method.saldo} 
                    />
                ))}
            </ul>

            <ul id='chart'></ul>

            <ul id="apartados">
                {records.map(record => (
                    <Budget 
                        key={record.id} 
                        name={record.nombre} 
                        amount={record.saldo} 
                        icon={record.icono} 
                        distribution={record.reparticion}
                    />
                ))}
            </ul>

        </div>
    );

}
