import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default () =>  {

    //METHODS
    const [balance, setBalance] = useState(null);

    useEffect(() => {
        const fetchBalance = async () => {
        try {
            const response = await fetch('https://finanzas.visssible.com/backend/apartados-suma-activos.php');
            const data = await response.json();
            setBalance(data.sum);
        } catch (error) {
            console.error('Error fetching balance:', error);
        }
        };

        fetchBalance();
    }, []);

    return(

        <li id="balance">
            <div class="founds"><i class="fas fa-wallet"></i> <span class="Label">Founds:</span> <span class="amount">$0</span></div>
            <div class="budgets"><i class="fas fa-chart-pie"></i> <span class="Label">Budgets:</span> <span class="amount">$0</span> </div>
            <div class="balance"><i class="fas fa-balance-scale"></i> <span class="Label">Balance:</span> <span class="amount">$0</span></div>
        </li>

    );

}
