import React, { useEffect, useState } from 'react';
import axios from 'axios';

import Budget from '../components/Budget';
import Method from '../components/Method';

export default () =>  {
    // STATES
    const [records, setRecords] = useState([]);
    const [methods, setMethods] = useState([]);
    const [sumOfAmounts, setSumOfAmounts] = useState(0);
    const [loading, setLoading] = useState(true);

    // FETCH DATA
    useEffect(() => {
        const fetchData = async () => {
            try {
                const recordsResponse = await axios.get('https://finanzas.visssible.com/backend/apartados-consultar.php');
                const methodsResponse = await axios.get('https://finanzas.visssible.com/backend/methods-consult.php');
                setRecords(recordsResponse.data);
                setMethods(methodsResponse.data);
                setLoading(false); // Set loading to false after data is fetched
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []); // This effect runs once on mount

    // Calculate sum of amounts
    useEffect(() => {
        const calculateSumOfAmounts = () => {
            let sum = 0;
            for (const record of records) {
                if (record.budget === 1) {
                    sum += parseFloat(record.amount);
                }
            }
            setSumOfAmounts(sum);
        };

        // Check if records array is not empty before calculating sum
        if (!loading && records.length > 0) {
            calculateSumOfAmounts();
        }
    }, [records, loading]); // This effect runs whenever the records or loading state changes

    // Calculate real amount
    let realAmount = 0;
    if (methods.length >= 5) {
        realAmount = (
            parseFloat(methods[0].saldo) +
            parseFloat(methods[1].saldo) +
            parseFloat(methods[2].saldo) +
            parseFloat(methods[3].saldo) +
            parseFloat(methods[4].saldo)
        ).toFixed(2);
    }   

    return (
        <div id="principal" className='pantalla completa' style={{paddingBottom: '66px'}}>
            <ul id='methods'>
                {methods.length > 0 && (
                    <li key="0" className="Account">
                        <i className="fas fa-money-check-alt"></i>
                        <div className="name">{methods[0].nombre}</div>
                        <div className="amount">{methods[0].saldo}</div>
                        <div className="realAmount">${realAmount}</div>
                    </li>
                )}

                <li id="balance">
                    <div className="founds"><i className="fas fa-wallet"></i> <span className="Label">Founds:</span> <span className="amount">$0</span></div>
                    <div className="budgets"><i className="fas fa-chart-pie"></i> <span className="Label">Budgets:</span> <span className="amount">${sumOfAmounts}</span> </div>
                    <div className="balance"><i className="fas fa-balance-scale"></i> <span className="Label">Balance:</span> <span className="amount">$0</span></div>
                </li>

                {methods.slice(1).map((method, index) => (
                    <Method 
                        key={index} 
                        name={method.nombre} 
                        amount={method.saldo} 
                    />
                ))}
            </ul>

            <ul id='chart'></ul>

            <ul id="apartados">
                {records.map(record => (
                    <Budget 
                        key={record.id} 
                        name={record.nombre} 
                        amount={record.saldo} 
                        icon={record.icono} 
                        distribution={record.reparticion}
                    />
                ))}
            </ul>
        </div>
    );
}
