import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useLocation } from 'react-router-dom';

import Registro from '../components/Registro';

const BudgetScreen = () => {
    //GET PARAMETERS
    const { name, date } = useParams();
    const location = useLocation();
    const { state } = location;

    const icon = state && state.icon ? state.icon : "envelop";
    const amount = state && state.formatedAmmount ? state.formatedAmmount : "";

    // Set a default value for date if not provided
    const formattedDate = date || "01-01-01";


    //GET DATA
    const [registros, setRegistros] = useState([]);

    const apiUrl = 'https://finanzas.visssible.com/backend/movimientos-consultar.php?apartado=Jobad';

    useEffect(() => {
    const fetchData = async () => {
    try {
        const response = await axios.get(apiUrl);
        setRegistros(response.data);
    } catch (error) {
        console.error('Error fetching data:', error);
    }
    };

    fetchData();
    }, []); // The empty dependency array ensures that this effect runs once on mount


  return (
    <div id="movimientos" className='pantalla completa'>

        <h3 class="apartado"><i class="fas fa-male"></i> Jobad <span class="saldo">$0.00</span></h3>

        <div>
            <h1>Budget Screen</h1>
            <p>Name: {name}</p>
            <p>Date: {formattedDate}</p>
            <p>Icon: {icon}</p>
            <p>Amount: {amount}</p>
        </div>

        <ul id='lista'>
            {registros.map(registro => (
                <Registro 
                    key={registro.id}
                    description={registro.descripcion}
                    amount={registro.cantidad}
                    tipo={registro.tipo}
                    method={registro.method}
                    apartado={registro.apartado}
                    highlighted={registro.highlight}
                    fecha={registro.fecha_mov}
                />
            ))}
        </ul>

    </div>
  );
};

export default BudgetScreen;
