import React, { useState } from 'react';
import axios from 'axios';

export default () =>  {

  // State variables to hold form data
  const [cantidad, setCantidad] = useState('');
  const [apartado, setApartado] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [method, setMethod] = useState('');
  const [fechaMov, setFechaMov] = useState('');

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Create a new movimiento object with form data
      const newMovimiento = {
        cantidad,
        apartado,
        descripcion,
        method,
        fecha_mov: fechaMov // Make sure to match the field name with the API
      };

      // Send a POST request to the API endpoint to add a new movimiento
      const response = await axios.post('http://localhost:3000/api/movimientos', newMovimiento);

      // Log the response or handle success as needed
      console.log('New movimiento added:', response.data);

      // Clear form fields
      setCantidad('');
      setApartado('');
      setDescripcion('');
      setMethod('');
      setFechaMov('');
    } catch (error) {
      console.error('Error adding new movimiento:', error);
      // Handle error
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={cantidad} onChange={(e) => setCantidad(e.target.value)} placeholder="Cantidad" />
      <input type="text" value={apartado} onChange={(e) => setApartado(e.target.value)} placeholder="Apartado" />
      <input type="text" value={descripcion} onChange={(e) => setDescripcion(e.target.value)} placeholder="Descripción" />
      <input type="text" value={method} onChange={(e) => setMethod(e.target.value)} placeholder="Método" />
      <input type="date" value={fechaMov} onChange={(e) => setFechaMov(e.target.value)} />
      <button type="submit">Add Movimiento</button>
    </form>
  );
}
