import React, { useEffect, useState } from 'react';
import axios from 'axios';

import Budget from '../components/Budget';
import Method from '../components/Method';

export default () =>  {

    //RECORDS
    const [records, setRecords] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('https://finanzas.visssible.com/backend/apartados-consultar.php');
                setRecords(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []); // The empty dependency array ensures that this effect runs once on mount


    //METHODS
    const [methods, setMethods] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('https://finanzas.visssible.com/backend/methods-consult.php');
                setMethods(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []); // The empty dependency array ensures that this effect runs once on mount

    // Define the desired order of items
    const desiredOrder = ["Account", "Online CC", "Jobad CC", "Maddie CC", "Respaldo CC", "Amazon CC",  "PayPal CC", "Cash", "Savings", "Stock"];

    // Create a new array to store the reordered methods
    const reorderedMethods = [];

    // Iterate over the desired order and push matching items from the original array
    desiredOrder.forEach(name => {
        const method = methods.find(method => method.nombre === name);
        if (method) {
            reorderedMethods.push(method);
        }
    });


    return (
        <div id="principal" className='pantalla completa' style={{paddingBottom: '66px'}}>

            <ul id='methods'>
                {reorderedMethods.slice(1).map((method, index) => (
                    <Method 
                        key={index} 
                        name={method.nombre} 
                        amount={method.saldo} 
                    />
                ))}
            </ul>

            <ul id='chart'></ul>

            <ul id="apartados">
                {records.map(record => (
                    <Budget 
                        key={record.id} 
                        name={record.nombre} 
                        amount={record.saldo} 
                        icon={record.icono} 
                        distribution={record.reparticion}
                    />
                ))}
            </ul>

        </div>
    );

}
