import React, { useEffect, useState } from 'react';
import axios from 'axios';

import Budget from '../components/Budget';
import Method from '../components/Method';

export default () =>  {

    const [apartados, setApartados] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
        try {
            const response = await axios.get('https://finanzas.visssible.com/backend/apartados-consultar.php?');
            setApartados(response.data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
        };

        fetchData();
    }, []); // The empty dependency array ensures that this effect runs once on mount


    return (
        <div id="principal" className='pantalla completa' style={{paddingBottom: '66px'}}>

            <ul id='methods'>
                {methods.map((method, index) => (
                    <Method 
                        key={index} 
                        name={method.nombre} 
                        amount={method.saldo} 
                    />
                ))}
            </ul>

            <ul id='chart'></ul>

            <ul id="apartados">
                {apartados.map(apartado => (
                    <Budget 
                        key={apartado.id} 
                        name={apartado.nombre} 
                        amount={apartado.saldo} 
                        icon={apartado.icono} 
                        distribution={apartado.reparticion}
                    />
                ))}
            </ul>

        </div>
    );

}
